
public class ForLoop {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//for each
				char[] letter = new char[3];
				letter[0] = 'A';
				letter[1] = 'B';
				letter[2] = 'C';
				for (char result: letter){
					System.out.println(result);
				}
	}

}
